{{
  (() => {
    const USER_DASH = "https://courierbi.pathaointernal.com/public/dashboard/ffd75f53-d614-4acb-b235-f2a81884daab";
    const ADMIN_DASH = "https://courierbi.pathaointernal.com/public/dashboard/52779b3a-262e-4120-bcb8-5a748ddb6f5f";

    const data = getUserDetailsByEmail?.data;

    // DEFAULT while loading or if query failed => user dashboard (no id needed)
    if (!Array.isArray(data) || data.length === 0) return USER_DASH;

    const user = data[0];

    // Admin override
    if (user?.is_admin === true) return ADMIN_DASH;

    // Non-admin with id (falls back to empty if missing)
    const id = user?.id ?? "";
    return `${USER_DASH}?id=${id}`;
  })()
}}
