{{
  followupResponse.data.length > 0
    ? followupResponse.data
        .map((response, index) => {
          let jsonData = typeof response?.response === "string"
            ? JSON.parse(response?.response || '{}') 
            : response?.response; 
const filteredJsonData = Object.fromEntries(
     Object.entries(jsonData).filter(([key]) => !["business_team_user_id", "tracker_scope", "competitor"].includes(key) //
)
          );

return `<div style="background-color: #fff; margin: 0px; padding: 0px; 
border-radius: 1px; rgba(0, 0, 0, 0.1);"> 
<h2 style="margin: 0; padding: 15px 10px 15px; font-weight: bold; text-align: left; 
background-color: #e0e0e0; font-size: 15px; border-radius: 0px 0px 0 0;">Response ${index + 1}
</h2>
<table style="width: 100%; border-collapse: collapse; font-size: 14px; 
                           margin: 0; padding: 0;">
<tr style="background-color: #f4f4f4;">
<th style="border: 1px solid #ddd; padding: 8px; text-align: left;">Field</th>
<th style="border: 1px solid #ddd; padding: 8px; text-align: left;">Value</th>
</tr>

${Object.entries({
"New Onboarded Merchant ID": response?.new_onboards_id,


"Created At": response?.created_at,

"Competitor": response?.competitor,
    ...Object.fromEntries(
        Object.entries(filteredJsonData).map(([key, value]) => 
            [key.replace(/_/g, " ")                 .replace(/\b\w/g, char => char.toUpperCase()), value]
        )
    )
  })
  .map(([key, value]) => 
    value && value !== "N/A" 
      ? `<tr>
           <td style="border: 1px solid #ddd; padding: 8px; font-weight: bold;">${key}</td>
           <td style="border: 1px solid #ddd; padding: 8px;">${value}</td>
         </tr>`
      : ""
  ).join("")}
</table>
</div>`;
        })
        .join("")
    : "<p style='text-align: center; color: #333;'>No records found.</p>"
}}
